import xmlrpc.client

url, username, pwd, dbname = 'http://127.0.0.1:9000', "admin", "admin", "hotel3"
common = xmlrpc.client.ServerProxy('{}/xmlrpc/2/common'.format(url))
uid = common.login(dbname, username, pwd)
model = xmlrpc.client.ServerProxy('{}/xmlrpc/2/object'.format(url))
print("Python")
print("Logged in as %s (uid:%d)" % (username, uid))
#search
sale_state_orders = model.execute_kw(dbname, uid, pwd, 'sale.order', 'search',
    [[['state', '=', 'sale']]])
print("Sale state orders : ", sale_state_orders)
# search_count
sale_state_orders = model.execute_kw(dbname, uid, pwd, 'sale.order', 'search_count',
    [[['state', '=', 'sale']]])
print("Sale state orders : ", sale_state_orders)

default_signature = ''

for ids in sale_state_orders:
	model.execute_kw(dbname, uid, pwd, 'sale.order', 'write', [[ids], {'sign': default_signature}])



